document.addEventListener("DOMContentLoaded",(()=>{const container=document.getElementById("menu-container"),savedMenu=localStorage.getItem("savedMenu");if(!savedMenu)return void(container.innerHTML="<p>Aucun menu n'est sauvegard√©.</p>");const menuData=JSON.parse(savedMenu);let html='<ul class="w-full p-4">';for(const[repas,data]of Object.entries(menuData))data&&(html+=`<li class="pl-4 mb-4 w-full bg-white rounded shadow-md flex justify-between items-center"><strong>${repas} :</strong> `,data.url?html+=`<a href="${data.url}" class="bg-green-600 p-4 rounded text-white font-bold w-1/2 text-center hover:bg-green-600">${data.nom}</a>`:html+=`<em style="color: red;">${data.nom}</em>`,html+="</li>");html+="</ul>",container.innerHTML=html}));